css:
@media print{@page {size: landscape}}

.print-format{
    font-size:12px!important;
    min-width:95%!important;
}
.filter-section{
    border:none !important;
}
.body-report-style td , tfoot td{
     border:1px solid #bbbbbb;
}
.print-format td, .print-format th{
    padding:4px 2px !important;

}

.print-format th{
    color:#000!important;
    border-bottom: solid 1px #000;
    background:#ccc;
}
.print-format .letter-head{
    margin:0px !important;
}
h2{
    margin:0px!important;
}

.nowrap{
    white-space: nowrap!important;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">
    <h2>Manager Flash Report</h2>
</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set data = frappe.call("frappe.desk.query_report.run",
        report_name="Manager Flash Report",
        filters={
            "date":frappe.form_dict.get('start_date'),
            "property":doc.name,
        }
    ) %}

<table style="width:100%;white-space:nowrap;" class="w-100 report-list body-report-style">
    <thead>
        <tr>
            {%for c in data.columns%}
                <th class="text-{{c.align}}">{{c.label}}</th>
            {%endfor%}
        </tr>
    </thead>
    <tbody>
        {% for g in data.result | selectattr('indent','equalto', 0) | list %}
        <tr>
            <td style="font-size:13px;font-weight:700;margin:5px;" colspan='8' >{{g.title}}</td>
        </tr>
        {% for d in data.result | selectattr('group','equalto', g.title) | list %}
        <tr>
            {%for c in data.columns%}
                <td class="text-{{c.align}}" style="margin:5px">
                    {{frappe.format(d[c.fieldname],{"fieldtype":c.fieldtype})}}
                </td>
            {%endfor%}
        </tr>

        {% endfor %}
        {% endfor %}
    </tbody>
</table>"}]
