[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set data= frappe.call("frappe.desk.query_report.run",
        report_name="Reservation Forecast",
        filters={
            "start_date":frappe.form_dict.get('start_date'),
            "end_date":frappe.form_dict.get('end_date'),
            "property":doc.name,
            "room_type":frappe.form_dict.get('room_type'),
            "business_source":frappe.form_dict.get('business_source'),
            "business_source_type":frappe.form_dict.get('business_source_type'),
            "guest_type":frappe.form_dict.get('guest_type'),
            "reservation_type":frappe.form_dict.get('reservation_type'),
            "row_group":"" if  not frappe.form_dict.get('row_group') else frappe.form_dict.get('row_group'),
           
        }
    )
%}
{% if (data.result|length) >0 %}
<table class='report-list occupied-001' style='width:100%'>
    <tr>
        <th class="text-center">No.</th> 
        {%for c in data.columns%}
        <th style="width:{{c.width}}px;" class="{{c.css_class or ''}} text-{{c.align}}">{{c.label}}</th>
        {%endfor%} 
    </tr>
    {% if frappe.form_dict.get('row_group') != "Date" %}
    {% for d in data.result | selectattr('indent','equalto',0)| list %}
       <tr>
           <td>{{loop.index}}</td>
            {% for c in data.columns %}
                <td style="width:{{c.width}}px;font-size:12px;" class="{{c.css_class}} text-{{c.align}}">{{frappe.format(d[c.fieldname or ''],{"fieldtype":c.fieldtype})}}</td>
            {% endfor %}
       </tr>
    {% endfor %}
        {% for g in data.result %}
            {% if g.indent == 1 %}
            <tr>
                <td class="text-center">{{loop.index}}</td>
                {% for c in data.columns %}
                <td style="width:{{c.width}}px;" class="{{c.css_class}} text-{{c.align}}">{{frappe.format(g[c.fieldname or ''],{"fieldtype":c.fieldtype})}}</td>
                {% endfor %}
            </tr>
            {% endif %}
            {% if g.is_total_row == 1 %}
            <tr style="border-top:dotted 1px #000000">
                <td></td>
                {% for c in data.columns %}
                <td style="width:{{c.width}}px;font-size:13px;" class="{{c.css_class}} text-{{c.align}}"><strong>{{frappe.format(g[c.fieldname or ''],{"fieldtype":c.fieldtype})}}</strong></td>
                {% endfor %}
            </tr>
            {% endif %}
        {% endfor %}
    
    {% endif %}
    {% if frappe.form_dict.get('row_group') == 'Date' %}
        {% for g in data.result %}
            {% if g.indent == 1 %}
            <tr>
                <td class="text-center">{{loop.index}}</td>
                {% for c in data.columns %}
                <td style="width:{{c.width}}px;" class="{{c.css_class}} text-{{c.align}}">{{frappe.format(g[c.fieldname or ''],{"fieldtype":c.fieldtype})}}</td>
