[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set guest_detail = frappe.db.sql("select * from `tabReservation Stay` 
where guest = '{}'".format(doc.name),as_dict=1)%}
<table class="report-list w-100 mt-2">
    <tr>
        <td class="text-center">{{_("Reservation No.")}}</td>
        <td class="text-center">{{_("Arrival")}}</td>
        <td>{{_("Room")}}</td>
        <td class="text-center">{{_("Departure")}}</td>
        <td class="text-center">{{_("Status")}}</td>
    </tr>
    {% for rs in guest_detail %}
    <tr>
        <td class="text-center"><a onclick="window.parent.postMessage('view_reservation_stay_detail|{{rs.reservation}}', '*')">{{rs.name}}</a></td>
        <td class="text-center">{{rs.arrival_date}}</td>
        <td>
            {% set rooms = rs.rooms.split(',') %}
            {% set room_type_alias = rs.room_type_alias.split(',') %}
            {% set comas = ',' %}
            {% for r in rooms %}
                <span>
                    {% if room_type_alias[loop.index0] %}{{ room_type_alias[loop.index0] }}{% endif %}/{% if r %}{{r}}{% else %}<a onclick="window.parent.postMessage('view_reservation_stay_detail|{{rs.name}}', '*')" class='p-0 link_line_action1'>unassign</a>{% endif %}
                </span>{% if loop.revindex==1 %}{% else %}{{comas|trim}}{% endif %}
            {% endfor %}
        </td>
        <td class="text-center">{{rs.departure_date}}</td>
        <td class="text-center">
            <span class="rounded-pill py-1 px-2 text-white" style="background:{{rs.status_color}}" >{{rs.reservation_status}}</span>    
        </td>
    </tr>
    {% endfor %}
</table>"}]

css:
.header-name{
    cursor: pointer;
    padding: .5rem;
    font-size: 13px;
    font-weight: 600 !important;
    background-color: rgb(226 232 240);
    color: #343a40;
}

*{
    color:#000!important;
}
table{
    border:none!important;
}
.report-list {
    margin-top: 0px !important;
    margin-bottom: 10px !important;
}
.report-list td,.tbl-payment-list td {
    padding-top: 6px!important;
    padding-bottom: 6px!important;
}
.tbl-list tr td,.tbl-list-summary table tr td {
    padding: 4px 0px !important;
    font-size: 13px !important;
    border:none!important;
}
.tbl-list tr td:nth-child(2), .tbl-list-summary table tr td:nth-child(2){
    padding-left: 4px !important;
    padding-right: 4px !important;
}
.tbl-list-right tr td:nth-child(3), .tbl-list-summary table tr td:nth-child(3){
    text-align: right;
}
.report-list thead {
    background: silver;
    border-bottom:solid 1px #000!important;
}
.report-list tfoot {
    border-top:double 1px #000!important;
}
.report-list thead tr th {
    color: #000;
    font-weight: 600;
}
.report-list th, .report-list td {
    border: 1px!important
}
.tbl-list-summary table tr td:nth-child(3) {
    min-width: 100px;
}
.tbl-payment-list tbody td {
    border-top: none !important;
}

.print-format .row:not(.section-break)
{
    margin-top:5px!important;
}
.print-format .letter-head{

    margin-bottom:5px!important;
}
hr{
  border-bottom: solid 1px #000;
  border-top: solid 0px #000;
    margin: 0px;
}
.print-heading{
    border-bottom:solid 1px #000!important;
}
.border{
    border-bottom:solid 1px #000!important;
    padding:0px!important;
}

.print-format td, .print-format th{
    padding: 4px 0px!important;
}

.print-format .table-bordered td, .print-format .table-bordered th{
    border:1px solid #000!important;
